.container {
    display: flex; /* Verwenden Sie Flexbox */
    align-items: center; /* Zentrieren Sie die Elemente vertikal */
}

.fold-line1 {
    width: 4%; /* Breite der Linie (entsprechend der gesamten Seitenbreite) */
    height: 0.8px; /* Höhe der Linie */
    background-color: #000; /* Farbe der Linie, z.B. schwarz */
    position: absolute;
    top: 35.8%; /* Positioniert die Linie in der Mitte der Seite */
    left: -20px;
}

